-- ----------------------------
-- Table structure for `t_comment`
-- ----------------------------
DROP TABLE IF EXISTS `t_comment`;
CREATE TABLE `t_comment` (
  `ID` int(11) NOT NULL auto_increment,
  `ISBN` varchar(15) default NULL COMMENT '图书的ISBN',
  `COM_USER` varchar(45) default NULL COMMENT '评论的用户名',
  `COM_AVATAR` varchar(150) default NULL COMMENT '用户的图像',
  `COM_CONTENT` text COMMENT '书评内容',
  `CREATE_AT` varchar(45) default NULL COMMENT '书评创建时间',
  `COM_ZHANDIAN` varchar(10) default NULL COMMENT '书评来自的站点',
  PRIMARY KEY  (`ID`),
  KEY `isbn` USING BTREE (`ISBN`)
) ENGINE=MyISAM DEFAULT CHARSET=utf8 ROW_FORMAT=DYNAMIC;

